package com.tech;

class Employee4{
	
	private final int id;
	private final String name;
	private final double salary;
	
	Employee4(int id , String name , double salary){
		this.id = id; 
		this.name = name;
		this.salary = salary;
	}
	
	public Integer getId() {
		return this.id;
	}
	public String getName() {
		return this.name;
	}
	public double getSalary() {
		return this.salary;
	}
}


